// Navigation.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function Navigation() {

  const [user, setUser] = useState(null);

  useEffect(() => {
    // Fetch user data from the server
    const fetchUser = async () => {
      try {
        const response = await fetch('http://localhost:3001/api/auth/user/65bb755dec385a94ab2528a8', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            // Include any authentication headers if needed
            // 'Authorization': 'Bearer yourAuthToken',
          },
          // Include credentials: 'include' if you are using cookies for authentication
          // credentials: 'include',
        });

        if (response.ok) {
          const userData = await response.json();
          setUser(userData);
        } else {
          // Handle error
          console.error('Failed to fetch user data:', response.statusText);
        }
      } catch (error) {
        console.error('Error during user data fetch:', error.message);
      }
    };

    // Call the fetchUser function
    fetchUser();
  }, []); // Empty dependency array means this effect runs once after the initial render

  return (
    <nav>
      <ul>
        <li>
          <Link to="/">Home</Link>
        </li>
        <li>
          <Link to="/login">Login</Link>
        </li>
        <li>
          <Link to="/register">Register</Link>
        </li>
          <li><Link to="/upload">Upload Image</Link></li>
          {user && (
          <li>
            {/* Display user information */}
            Logged in as {user.username} (ID: {user.id})
          </li>
        )}
      </ul>
    </nav>
  );
}

export default Navigation;
