// Navigation.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { jwtDecode } from 'jwt-decode';

function Navigation() {
  const { state } = useAuth();
  const { user } = state;

  const [email, setEmail] = useState('');

  useEffect(() => {
    // Retrieve the token from localStorage (you may need error handling here)
    const token = localStorage.getItem('token');

    if (token) {
      // Decode the token to extract user data (e.g., email)
      const decodedToken = jwt_decode(token);

      // Extract the email from the decoded token
      const userEmail = decodedToken.email;

      // Update the state to display the email
      setEmail(userEmail);
    }
  }, []);
  return (
    <nav>
      <ul>
        <li>
          <Link to="/">Home</Link>
        </li>
        <li>
          <Link to="/login">Login</Link>
        </li>
        <li>
          <Link to="/register">Register</Link>
        </li>
          <li><Link to="/upload">Upload Image</Link></li>
          {user && (
          <li>
            <span>Welcome, {user.email}, {user.username}!</span>
          </li>
        )}
              <h2>User Profile</h2>
      <p>Email: {email}</p>
      </ul>
    </nav>
  );
}

export default Navigation;
